// MAIN
export const BASE_WIDTH: number = 1000;
export const BASE_HEIGHT: number = 500;

// PADDLE
export const BASE_PADDLE_SPEED: number = BASE_HEIGHT / 50;

export const BASE_PADDLE_LEFT_POS_X: number = BASE_WIDTH / 50;
export const BASE_PADDLE_LEFT_POS_Y: number = BASE_HEIGHT / 2;

export const BASE_PADDLE_RIGHT_POS_X: number = BASE_WIDTH - (BASE_WIDTH / 50);
export const BASE_PADDLE_RIGHT_POS_Y: number = BASE_HEIGHT / 2;

export const BASE_PADDLE_SIZE_X: number = BASE_WIDTH / 300;
export const BASE_PADDLE_SIZE_Y: number = BASE_HEIGHT / 8;
export const BASE_PADDLE_SIZE_Y_MIN: number = BASE_HEIGHT / 30;


// BALL
export const BASE_BALL_SPEED: number = BASE_WIDTH / 100;
export const BASE_BALL_SIZE: number = BASE_WIDTH / 200;

export const BASE_BALL_POS_X: number = BASE_WIDTH / 2;
export const BASE_BALL_POS_Y: number = BASE_HEIGHT / 2;

// COLOR
export const BASE_COLOR_FILL: string = "#59A80F";
export const BASE_COLOR_BOARD: string = "#000000";
export const BASE_COLOR_SHADOW: string = "#9ED54C";
